import React, {useState} from 'react';
import './AjouterModifierMatInfo.css'


function AjouterModifierMatInfo(props) {
    //state
    const [nom_produit, setNomProduit] = useState("");
    const [versionProduit, setVersionProduit] = useState("");
    const [refProduit, setRefProduit] = useState("");
    const [prixProduit, setPrixProduit] = useState("");
    const [descriptionProduit, setDescriptionProduit] = useState("");

    //comportement


    //render
    return (
        <div className="body">
            <div className="containerForm">
                <h1>Ajouter un materiel informatique</h1>
                <h3>Photo du produit</h3>
                <div className="setImage">
                    <div className="imageici"></div>
                    <div className="textSetImage">
                        <h4>Charger une photo</h4>
                        <p className="legendPhotoFormat">format PNG ou JPG</p>
                        <div className="buttonSetImage">
                            <div className="button OkButton">Choisir Image</div>
                            <div className="button NoButton">Supprimer Image</div>
                        </div>
                    </div>
                </div>
                <form action="" className="setMatInfo">

                    <input type="text" placeholder="Nom" className="input-Nom"/>
                    <input type="text" placeholder="Version" className="input-version"/>
                    <input type="text" placeholder="Reference" className="input-ref"/>
                    <input type="text" placeholder="Prix" className="input-prix"/>
                    <input type="text" placeholder="Description" className="input-description"/>
                    <div className="ButtonSubmit">
                        <div className="button NoButton">Annuler</div>
                        <div className="button OkButton">Ajouter</div> {/*devient confirmer si on est en modification*/}
                    </div>

                </form>
            </div>

        </div>
    );
}

export default AjouterModifierMatInfo;
