import React, { useState } from 'react';
import "./Navbar.css";
import logo_icon from "../assets/logo_icon.PNG"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser } from "@fortawesome/free-solid-svg-icons";

function Navbar({ isConnected, isAdmin }) {
    // state
    const [selected, setSelected] = useState("home");

    // comportement
    const handleSelection = (selection) => {
        setSelected(selection);
    };

    const handleClickLogo = ()=> {
        //page Accueil
    }

    // render
    return (
        <nav>
            <div className="liste_element_navbar">
                <div onClick={handleClickLogo} ><img src={logo_icon} alt="logo"/></div>
                <div onClick={() => handleSelection("home")} className={`element_navbar ${selected === "home" ? "selected" : ""}`}><p> Home</p></div>

                {isAdmin && (
                    <div onClick={() => handleSelection("ajouterMateriels")} className={`element_navbar ${selected === "ajouterMateriels" ? "selected" : ""}`}><p>Ajouter materiels</p></div>
                )}

                <div onClick={() => handleSelection("historiquesEmprunts")} className={`element_navbar ${selected === "historiquesEmprunts" ? "selected" : ""}`}><p>Historiques des emprunts</p></div>

                {isAdmin && (
                    <div onClick={() => handleSelection("consultationUtilisateurs")} className={`element_navbar ${selected === "consultationUtilisateurs" ? "selected" : ""}`}><p>Consultation utilisateurs</p></div>
                )}

                {isConnected !== true ?
                    <div onClick={() => handleSelection("connexion")} className={`element_navbar ${selected === "connexion" ? "selected" : ""}`}><p><FontAwesomeIcon icon={faUser} /> Connexion</p></div>
                    : <div onClick={() => handleSelection("account")} className={`element_navbar ${selected === "account" ? "selected" : ""}`}> <p><FontAwesomeIcon icon={faUser} /> Mon Profil </p> </div>
                }

            </div>
        </nav>
    );
}

export default Navbar;
